import $ from 'jquery';

export default class EventModel {
    async getAll(userId, week) {
        return new Promise( (resolve, reject) => {
            $.get('/mockRequests/planItems.json', {id: userId, week: week}).done((result) => {
                resolve(result);
            })
                .fail((err) => {
                    reject(err);
                })
        })
    }

    async get(id) {
        return new Promise( (resolve, reject) => {
            $.get('/mockRequests/taskInfo.json').done((result) => {
                resolve(result);
            })
                .fail((err) => {
                    reject(err);
                })
        })
    }

    async add(params) {
        return new Promise( (resolve, reject) => {
            $.get('/mockRequests/saveTask.json', params).done((result) => {
                resolve(result);
            })
                .fail((err) => {
                    reject(err);
                })
        })
    }

    async change(id, params) {

        const data = {id: id, ...params};

        return new Promise( (resolve, reject) => {
            $.get('/mockRequests/changeTask.json', data).done((result) => {
                resolve(result);
            })
                .fail((err) => {
                    reject(err);
                })
        })
    }
}
