import $ from 'jquery';

export default class EventModel {
    async getAll() {
        return new Promise( (resolve, reject) => {
            $.get('/mockRequests/getTasks.json').done((result) => {
                resolve(result);
            })
                .fail((err) => {
                    reject(err);
                })
        })
    }

    async getOne(id) {
        return new Promise( (resolve, reject) => {
            $.get('/mockRequests/getOneTask.json', {id: id}).done((result) => {
                resolve(result);
            })
                .fail((err) => {
                    reject(err);
                })
        })
    }
}
