import React, {Component} from 'react';
import RadioGroup from '@material-ui/core/RadioGroup';
import Radio from '@material-ui/core/Radio';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import PropTypes from 'prop-types';
import {EmptyFunc} from 'services/helper';

class ControlType extends Component {

    static propTypes = {
        value: PropTypes.number,
        onChange: PropTypes.func
    };

    static defaultProps = {
        value: null,
        onChange: EmptyFunc
    };

    onChange = (event) => {
      this.props.params.type = event.target.value;
    };

    render() {
        return (
            <div className={'control-type'}>
                <RadioGroup
                    row
                    name="color"
                    aria-label="color"
                    value={this.props.value}
                    initialvalue={this.props.value}
                    onChange={this.onChange}
                >
                    <FormControlLabel value={0} control={<Radio />} label="Запланировано" />
                    <FormControlLabel value={1} control={<Radio />} label="Фактически" />
                </RadioGroup>
            </div>
        );
    }
}

export default ControlType;
