import React, {Component} from 'react';
import RadioGroup from '@material-ui/core/RadioGroup';
import Radio from '@material-ui/core/Radio';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import PropTypes from 'prop-types';
import {EmptyFunc} from 'services/helper';

class CounterDays extends Component {

    static propTypes = {
        value: PropTypes.number,
        onChange: PropTypes.func
    };

    static defaultProps = {
        value: null,
        onChange: EmptyFunc
    };

    onChange = (event) => {
        this.props.params.days = event.target.value;
    };

    render() {
        return (
            <div className={'counter-days'}>
                <RadioGroup
                    row
                    name="color"
                    aria-label="color"
                    value={this.props.value}
                    initialvalue={this.props.value}
                    onChange={this.onChange}
                >
                    <FormControlLabel value={1} control={<Radio />} label="1" />
                    <FormControlLabel value={5} control={<Radio />} label="5" />
                    <FormControlLabel value={7} control={<Radio />} label="7" />
                    <FormControlLabel value={14} control={<Radio />} label="14" />
                    <FormControlLabel value={31} control={<Radio />} label="31" />
                </RadioGroup>
            </div>
        );
    }
}



export default CounterDays;
