import React, {Component} from 'react';
import PlanItem from "components/PlanItem/PlanItem";

class Day extends Component {

    showPlanItems = () => {
        const planItems = this.props.planItems.filter((item) => {
           return (item.zone === this.props.zone || item.date === this.props.date);
        });

        return planItems.map((item, index) => (
            <div key={index} className={'day__item'}>
                <PlanItem {...item}/>
            </div>
        ));

    };

    render() {
        return (
            <div className={'day'}>
                <div className={`day__title ${this.props.active}`}/>
                <div className={'day__body'}>
                    {this.showPlanItems()}
                </div>
            </div>
        );
    }
}

export default Day;