import React, {Component} from 'react';
import Zone from "components/DaysList/Zone";
import AppDate from 'entities/date';
import {observer} from "mobx-react";
import "./DaysList.css";

const DaysList = observer(
    class DaysListClass extends Component {
        constructor(props) {
            super(props);
            this.appDateInstance = new AppDate();
        }

        nextDay = () => {
            this.appDateInstance.nextDay();
        };

        prevDay = () => {
            this.appDateInstance.prevDay();
        };

        nextWeek = () => {
            this.appDateInstance.nextWeek();
        };

        prevWeek = () => {
            this.appDateInstance.prevWeek();
        };

        render() {
            return (
                <div className={'days-list'}>
                    <div className={'days-list__header'}>
                        <button onClick={this.prevWeek}>{'<<'}</button>
                        <button onClick={this.prevDay}>{'<'}</button>
                        <button onClick={this.nextDay}>{'>'}</button>
                        <button onClick={this.nextWeek}>{'>>'}</button>
                    </div>
                    <div className={'days-list__body'}>
                        <Zone startDate={this.appDateInstance.date}
                              currentDate={this.appDateInstance.dateFormatted}
                              zoneId={'productive'}
                              name={'Продуктивное время'}
                              planItems={this.props.planItems}
                              planItemsArr={this.props.planItemsIds}
                              daysCount={this.props.daysCount}
                        />
                    </div>
                </div>
            );
        }
    }
);

export default DaysList;