import React, { Component } from 'react';
import Day from "components/Day/Day";

class Zone extends Component {

    getDays = () => {

        let days = [];

        for (let index = 0; index <= this.props.daysCount; index++) {
            const date = new Date(this.props.startDate.setDate(this.props.startDate.getDate() + index));

            days.push(
                (<div key={index} className={'days-list__item'}>
                    <Day date={date} items={this.props.items} zone={this.props.zone}/>
                </div>)
                )
        }

        return days;
    };

    render() {
        return (
            <React.Fragment>
                {this.getDays()}
            </React.Fragment>
        );
    }
}

export default Zone;