import React, { Component } from 'react';
import Slider from '@material-ui/lab/Slider';
import InputLabel from "@material-ui/core/InputLabel/InputLabel";

class CreateFormSlider extends Component {

    state = {
        value: this.props.value || 0
    };

    onChange = (event, value) => {
         this.setState({value: value});
         event.target.value = value;
         event.target.name = this.props.name;
         event.target.id = this.props.id;
         this.props.onChange(event);
    };

    render() {
        return (
            <React.Fragment>
                <InputLabel htmlFor={this.props.name}>{this.props.label}</InputLabel>
                <div className={'create-plan-item__slider'}>
                    <Slider
                        id={this.props.id}
                        name={this.props.name}
                        min={15}
                        value={this.state.value}
                        max={this.props.max || 300}
                        step={this.props.step || 15}
                        onChange={this.onChange}
                    />
                </div>
                <input type='text'
                       id={this.props.id}
                       name={this.props.name}
                       value={this.state.value}
                />
            </React.Fragment>
        );
    }
}

export default CreateFormSlider;