import React from 'react';
import TextField from '@material-ui/core/TextField';

const CreateFormTextField = (props) => {
    return (
        <div className={'create-plan-item__item'}>
            <TextField
                id={props.name}
                name={props.name}
                label={props.label}
                value={props.value}
                onChange={props.onChange}
                onBlur={props.onBlur}
                margin="normal"
                variant="outlined"
            />
        </div>
    );
};

export default CreateFormTextField;