import React from 'react';
import './Grid.css';

const GridItems = (count, step) => {
    let items = [];
    for (let index = 0; index < count; index++) {
        items.push(<div key={index} style={{top: step*index}} className={'grid__item'}/> )
    }
    return items;
};

const Grid = (props) => {
    return (
        <div className={'grid'}>
            {GridItems(Math.ceil(props.height/props.step), props.step)}
        </div>
    );
};

export default Grid;