import React, {Component} from 'react';


const filterBy = function (WrappedComponent, filter, value) {
    return class FilterBy extends Component {

        filterByProp() {
            return this.props.itemIds.filter(item => {
                return this.props.items[item][filter] == value;
            });
        }

        render() {
            return (
                <WrappedComponent {...this.props} items={this.props.items} itemIds={this.filterByProp()}/>
            );
        }
    }
};


export default filterBy;