import React, {Component} from 'react';


export default function withGetId(WrappedComponent) {
    return class WithGetId extends Component {

        state = {
            value: this.props.value
        };

        onChange = (event) => {
            this.props.onChange({target: {value: event.target.valueId, name: event.target.name, valueId: event.target.value}});
            this.setState({value: event.target.valueId})
        };

        render() {
            return <WrappedComponent {...this.props} value={this.state.value} onChange={this.onChange}/>
        }
    }
}
