import React from 'react';
import {calculateOffsetTop, calculateOffsetLeft} from 'services/helper';

const FixBottomContainer = ({show, elementSelector, offsetLeft = 0, offsetTop = 0, children}) => {

    let position = {};

    if (show) {
        const element = document.querySelector(elementSelector);

        if (element) {
            position = {left: calculateOffsetLeft(element) + offsetLeft, top: calculateOffsetTop(element) + element.offsetHeight + offsetTop};
        }
    } else {
        position = {left: -1000, top: -1000}
    }

    return (
        <div className={`fixed-panel ${show ? 'visible' : 'invisible'}`}
             style={{position: 'absolute', left: position.left + 'px', top: position.top + 'px', zIndex: 10}}>
            {children}
        </div>
    );
};

export default FixBottomContainer;
