import { serializeToGet } from "services/helper";

export default class ByServer {
    constructor(url, data) {
        this.url = url;
        this.data = data;
    }

    get() {
        return fetch(
            this.url + `?data=${serializeToGet(this.data)}`,
            {
                method: 'GET',
                body: undefined
            }).then(res => res.json())
            .catch(error => {
                throw new Error(error);
            });
    };

}