import { decorate, observable } from "mobx";

class controlPanel {

    user = 1;
    user2 = 2;
    type = 0;
    days = 5;
    splitCalendar = false;

    constructor(initialParams) {
        if (initialParams) {
            this.user = initialParams.user;
            this.user2 = initialParams.user2;
            this.type = initialParams.type;
            this.days = initialParams.days;
            this.splitCalendar = initialParams.splitCalendar;
        }
    }
}

export let controlPanelEntity = decorate(controlPanel, {
    user: observable,
    user2: observable,
    splitCalendar: observable,
    type: observable,
    days: observable,
});
