import { decorate, observable, action } from "mobx";

class controlPanel {

    user1 = 1;
    user2 = 2;
    type = 0;
    days = 5;
    splitCalendar = false;

    constructor(initialParams) {
        if (initialParams) {
            this.user1 = initialParams.user1;
            this.user2 = initialParams.user2;
            this.type = initialParams.type;
            this.days = initialParams.days;
            this.splitCalendar = initialParams.splitCalendar;
        }
    }

    toggleSplit() {
        this.splitCalendar = !this.splitCalendar
    }
}

export let controlPanelEntity = decorate(controlPanel, {
    user1: observable,
    user2: observable,
    splitCalendar: observable,
    type: observable,
    days: observable,
    toggleSplit: action,

});
