import moment from 'moment';
import { getMonday } from 'services/helper';
import {decorate, observable, computed, action} from "mobx";

class DateEntity {
    static date = getMonday(new Date(Date.now()));

    static get dateFormatted() {
        return moment(this.date).format("DD.MM.YYYY");
    }

    static setDate(date) {
        this.date = moment(date).format();
    }

    static nextDay() {
        this.date = moment(this.date).add(1, 'days').format();
    }

    constructor() {
        return null;
    }

    static getInstance() {
        return this;
    }

}


export default decorate(DateEntity, {
    date: observable,
    dateFormatted: computed,
    setDate: action,
    nextDay: action
})