
import {decorate, observable, computed, action} from "mobx";

class DraggableItem {
    index = null;
    drag = false;
    dragStart(index) {
        this.index = index;
        this.drag = true;
    }

    dragStop() {
        this.drag = false;
        let index = this.index;
        this.index = null;
        return index;
    }
}

export const DraggableItemEntity = decorate(DraggableItem, {
    index: observable,
    drag: observable
});