export default class GetData {
    constructor(dataSource) {
        if (!dataSource) throw new Error('GetData: Не указан источник данных');
        this.dataSource = dataSource;
    }

    async get() {
        return new Promise(async (resolve, reject) => {
            let result = await this.dataSource.get();
            console.log(result);
            if (result.status) {
                resolve(result);
            } else {
                reject(result);
            }
        })
    }
}